Item.method_code:
Innovator inn = this.getInnovator();

string idList = this.getProperty("idList", "");


Item file = inn.newItem("File", "get");
file.setAttribute("select", "id,filename");
file.setProperty("id", idList);
file.setPropertyCondition("id", "in");


Item locatedItems = file.createRelationship("Located", "get");
locatedItems.setAttribute("select", "id,related_id,file_version,related_id(id,vault_url)");

return file.apply();
